True.FAILURE_CODE:
(O:AnimCode) 0 > if{
	(A:CIRCUIT ON:#CIRCUIT_ID_PARK#, Bool) if{
		#PARK_SPEED# (>O:_AnimSpeed)
		-1 (O:_AnimSpeed) * (A:ANIMATION DELTA TIME, seconds) * (O:AnimCode) + (>O:NewAnimCode)
		(O:NewAnimCode) 0 < if{ 0 (>O:NewAnimCode) }
		(O:NewAnimCode) (>O:AnimCode)
	}
}
(O:AnimCode)

False.FAILURE_CODE:
(O:AnimCode) 0 > if{
	(A:CIRCUIT POWER SETTING:#CIRCUIT_ID_WIPERS#, percent over 100) 1 #MIN_SPEED_PERCENT# - * #MIN_SPEED_PERCENT# + #MAX_SPEED# * (>O:_AnimSpeed)
	(O:_GoForward) if{ 1 (>O:_AnimDirection) } els{ -1 (>O:_AnimDirection) }
	(O:_AnimDirection) (O:_AnimSpeed) * (A:ANIMATION DELTA TIME, seconds) * (O:AnimCode) + (>O:NewAnimCode)
	(O:NewAnimCode) 100 > if{
		100 (O:NewAnimCode) 100 % - (>O:NewAnimCode)
		1 (>O:_ChangedDirection)
	} 
	(O:NewAnimCode) 0 < if{
		0 (>O:NewAnimCode)
	}
	(O:_ChangedDirection) if{ 
		(O:_GoForward) ! (>O:_GoForward)
		0 (>O:_ChangedDirection)
	}
	(O:NewAnimCode) (>O:AnimCode)
}
(O:AnimCode)

UseTemplate.ANIM_CODE:
(A:CIRCUIT POWER SETTING:#CIRCUIT_ID_WIPERS#, percent over 100) #ONE_MINUS_MIN_SPEED# * #MIN_SPEED_PERCENT# + #MAX_SPEED# * (>O:_AnimSpeed)
(O:_GoForward) if{ 1 (>O:_AnimDirection) } els{ -1 (>O:_AnimDirection) }
(O:_AnimDirection) (O:_AnimSpeed) * (A:ANIMATION DELTA TIME, seconds) * (O:AnimCode) + (>O:NewAnimCode)
(O:NewAnimCode) 100 > if{
	100 (O:NewAnimCode) 100 % - (>O:NewAnimCode)
	1 (>O:_ChangedDirection)
} 
(O:NewAnimCode) 0 < if{
	(O:NewAnimCode) abs 100 % (>O:NewAnimCode)
	1 (>O:_ChangedDirection)
}
(O:_ChangedDirection) if{ 
	(O:_GoForward) ! (>O:_GoForward)
	0 (>O:_ChangedDirection)
}
(O:NewAnimCode)
